import "./UploadPage.css";
import Headers from "../components/header/Header";

function chooseFile(fileInput) {
    if (fileInput.file && fileInput.files[0]){
        var reader = new FileReader();

        reader.onload = funtion (e); {
            $('image').attr('src', e.target.result);
        }
        reader.readAsDataURL(fileInput.file[0]);
    }
}

function UploadPage() {
    return (
        <div>
            <Headers /> 
            <div className ="content">
                <h2> Upload</h2>
            </div>
            <form className="Form">
                <div className= "picture">
                    {/* <img src="https://cdn.pixabay.com/photo/2015/07/27/20/16/book-863418_1280.jpg" alt=""/> */}
                    <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.6.0/jquery.min.js"></script>
                    <script>chooseFile(fileInput)</script>
                </div>
            </form>
        </div>
    );
}

export default UploadPage;
