import "./UploadPage.css";
import Sidebar from "../DescripPage/sidebar/sidebar";
import Headers from "../components/header/Header";

function UploadPage() {
    // const test = {
    //     "title": tilte,
    //     "publishedDate": null,
    //     "description": description,
    //     "genres": [ 
    //     categories,
    //     ],
    //     "author": author
    //   }
    
    // useEffect(() => {
    // const postBook = async () => {
    //     const res = await axios.post("http://localhost:5000/book/post", test);
    //     setAuthor(res.data);
    // };
    // postBook();
    // }, []);
    
    return (
        <div>
            <Headers /> 
            <div className="main">
                <div className ="upload">
                    <div className ="content">
                        <h2> Upload</h2>
                    </div>
                    <form className="Form">
                        <div className= "picture">
                            <img src="picture.jpg" alt=""/>
                            <button type ="add"> Add Pic</button>
                        </div>
                        <h2> Title</h2>
                        <div className="input">
                            <input type = "text" name="" />
                        </div>
                        <h2> Author</h2>
                        <div className="input">
                            <input type = "text" name="" />
                        </div>
                        <h2> Description</h2>
                        <div className="input">
                            <input type = "description" name="" />
                        </div>
                        <h2> Categories</h2>

                        <div className = "tick">
                            <label><input type="checkbox" name = ""/></label>
                            <h3>Lifestyle</h3>
                            <label><input type="checkbox" name = ""/></label>
                            <h3>Self help</h3>
                            <label><input type="checkbox" name = ""/></label>
                            <h3>Novel</h3>
                            <label><input type="checkbox" name = ""/></label>
                            <h3>Sport</h3>
                        </div>
                        
                        <button type ="upload"> Upload</button>
                    </form> 
                </div>
                
            </div>
        </div>
    );
}

export default UploadPage;
