import './Register.css';
import axios from 'axios';
import { useEffect, useState } from "react";

const form = document.getElementById('form');
const username = document.getElementById('username');
const email = document.getElementById('email');
const password1 = document.getElementById('password');
const password2 = document.getElementById('password2');

form.addEventListener('submit', e => {
	e.preventDefault();
	checkInputs();
});

function checkInputs() {
	const usernameValue = username.value.trim();
	const emailValue = email.value.trim();
	const password1Value = password1.value.trim();
	const password2Value = password2.value.trim();
	
    //check username
	if(usernameValue === '')
		setErrorFor(username, 'Username cannot be blank');
	else
		setSuccessFor(username);
	
    //check email
	if(emailValue === '')
		setErrorFor(email, 'Email cannot be blank');
	else if (!isEmail(emailValue))
		setErrorFor(email, 'Not a valid email');
	else
		setSuccessFor(email);
	//check password
	if(password1Value === '')
		setErrorFor(password1, 'Password cannot be blank');
	else
		setSuccessFor(password1);
	
    //check confirm password
	if(password2Value === '')
		setErrorFor(password2, 'Password2 cannot be blank');
	else if(password1Value !== password2Value)
		setErrorFor(password2, 'Passwords does not match');
	else
		setSuccessFor(password2);
}

function setErrorFor(input, message) {
	const formControl = input.parentElement;
	const small = formControl.querySelector('small');
	formControl.className = 'form-control error';
	small.innerText = message;
}

function setSuccessFor(input) {
	const formControl = input.parentElement;
	formControl.className = 'form-control success';
}
	
function isEmail(email) {
	return /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(email);
}


function Login() {
    const [account, setAccount] = useState("");
  
    useEffect(() => {
        const fetchAccount = async () => {
            const res = await axios.get("http://localhost:5050/user");
            setAccount(res.data);
        };
        fetchAccount();
    }, []);

    function onClickBtn(){
        var arr = document.getElementsByTagName('input');
        var Username = arr[3].value;
        var flag = 1;
        account.map( check => {
            if(Username === check.username && flag === 1){
                document.write("Username already exists!")
                // alert("Username already exists!")
                flag = 0
            }
        })
        if(flag === 1){
            alert("Register successful!")
        }
    }

    return (
    <section>
        <div className = "img-bg">
            <img src = "./image.jpg" alt = "Picture"/>
        </div>
        <div className = "icon">
            <img src = "./BookStore.png" alt = "Logo"/>
        </div>
        <div className = "content">
            <div className = "form">
                <h2> Sign up </h2>
                <form action = "">
                    <div className = "input">
                        <span> First Name </span>
                        <input type = "text" name = ""/>
                        <i class="fas fa-check-circle"></i>
                        <i class="fas fa-exclamation-circle"></i>
                        <small>Error message</small>
                    </div>
                    <div className = "input">
                        <span> Last Name </span>
                        <input type = "text" name = ""/>
                        <i class="fas fa-check-circle"></i>
                        <i class="fas fa-exclamation-circle"></i>
                        <small>Error message</small>
                    </div>
                    <div className = "input">
                        <span> E-mail </span>
                        <input type = "text" name = ""/>
                        <i class="fas fa-check-circle"></i>
                        <i class="fas fa-exclamation-circle"></i>
                        <small>Error message</small>
                    </div>
                    <div className = "input">
                        <span> Username </span>
                        <input type = "text" name = ""/>
                        <i class="fas fa-check-circle"></i>
                        <i class="fas fa-exclamation-circle"></i>
                        <small>Error message</small>
                    </div>
                    <div className = "input">
                        <span> Password </span>
                        <input type = "currentpassword" name = ""/>
                        <i class="fas fa-check-circle"></i>
                        <i class="fas fa-exclamation-circle"></i>
                        <small>Error message</small>
                    </div>
                    <div className = "input">
                        <span> Confirm Password </span>
                        <input type = "currentpassword" name = ""/>
                        <i class="fas fa-check-circle"></i>
                        <i class="fas fa-exclamation-circle"></i>
                        <small>Error message</small>
                    </div>
                    <div className = "input">
                        <button type = "submit" onClick = {onClickBtn}> Register </button>
                    </div>
                    <div className = "input">
                        <p><a href="/#"> Sign in </a> If you have an account</p>
                    </div>
                </form>
                <div className = "footer">
                    &copy;
                    2022
                    Bookaholic Inc
                </div>
            </div>
        </div>
    </section>
    );
}

export default Login;
